import React, { useCallback } from 'react';
import PropTypes from 'prop-types';
import Link from 'next/link';
import { Menu, Input, Row, Col } from "antd";
import Router from 'next/router';
import LoginForm from './LoginForm';
import UserProfile from './UserProfile';
import { useSelector } from "react-redux";
import useInput from '../hooks/useInput';
import { NodeExpandOutlined, SendOutlined, UserOutlined } from '@ant-design/icons';

const navContainerStyle={
    display: "flex",
    flexDirection: "row",
    width: "70%",
    background: "white",
    listStyleType: NodeExpandOutlined, /*목록 기호 제거 */
}
const AppLayout = ({children}) => {
    const { me } = useSelector((state)=>state.user);
    const [searchInput, onChangeSearchInput] = useInput('');
    const onSearch = useCallback(()=>{
        Router.push(`/hashtag/${searchInput}`);
    }, [searchInput]);

    return(
        <div>
            <nav>
                <ul className="nav-container" style={{navContainerStyle}}>
                    <li className="home" style={{ float: "left"}}><Link href="/main"><a><h1 style={{fontFamily: "Playfair Display, serif"}}>Chatter</h1></a></Link></li>
                    <li className="search" style={{ float: "left"}}><Input.Search placeholder="input search text" onSearch={onSearch} style={{ width: 200 }} /></li>
                    <li className="message" style={{ float: "left"}}><SendOutlined /></li>
                    <li className="profile" style={{ float: "left"}}><UserOutlined /></li>
                </ul>
            </nav>
            <Row gutter={8}>
                <Col xs={24} md={6}>
                    {me ? <UserProfile /> : <LoginForm />}
                </Col>
                <Col xs={24} md={12}>
                {children}
                </Col>
                <Col xs={24} md={6}>
                <a href="https://www.naver.com" target="_blank" rel="noreferrer noopener">Made by Minji</a>
                </Col>
            </Row>
        </div>
    );
};

AppLayout.propTypes = {
    children: PropTypes.node.isRequired,
};

export default AppLayout;
