import { useRouter } from 'next/router';
import React from 'react';

const HomeBar = () => {
    const router = useRouter();

    const toTop = () => {
        router.push('/main');
        window.scrollTo({top:0, left:0, behavior:'auto'});
    };

    return (
        <>
        <h1>왜안나와</h1>
        <div style={{top: "0", position: "fixed", width: "41.55%", borderBottom: "solid thin #F5F5F5", backgroundColor:"white", zIndex: "1", cursor: "pointer"}} onClick={toTop}>
                <h1 style={{fontSize: "25px", marginLeft:"15px"}}>Home</h1>
        </div>
        </>
    );
}

export default HomeBar;
