import React from 'react';
import { Card, Popover, Button } from 'antd';
import { MessageOutLined, EllipsisOutlined, HeartOutlined, RetweetOutlined } from '@ant-design/icons';

const PostCard = ({ post }) => {
    return(
        <div>
            <Card
            cover={post.Images[0] && <PostImages images={post.Images}/>}
            actions={[
                <RetweetOutlined key="retweet" />,
                <HeartOtluined key="heart" />,
                <MessageOutLined key="comment"/>,
                <Popover key="more">
                    <EllipsisOutlined />
                </Popover>,
            ]}
            >
                <Image />
                <Content />
                <Buttons>

                </Buttons>
            </Card>
            <CommentForm />
            <Comments />
        </div>          
    );
};

export default PostCard;
