import Chat from './../../components/Chat';
import DesktopLayout from './../../components/layout/DesktopLayout';
import { useJoinRoom } from '../../hooks/chatHooks';
import { socket } from './../../utils/io';
/*
const roomSocket = io.connect('http://localhost:3065/room', {
    path: '/socket.io',
    withCredentials: true,
});
*/

/*
const chatSocket = io.connect('http://localhost:3065/chat', {
    withCredentials: true,
});
*/

const Message = () => {
    const router = useRouter();
    const { roomId } = router.query;
    useJoinRoom(socket);

    return(
        <DesktopLayout Side={<Room height={document.documentElement.clientHeight}/>}>
            <Chat roomId={roomId} />
        </DesktopLayout>
    );
}

export default Message;
