import { useEffect, useMemo, useState } from 'react';

export const useJoinRoom = (socket, id) => {
    const requestJoin = () => {
       
        socket.emit('joinRoom', id);

        /*
        return () => {
            console.log(`Leave Room: ${roomId}`);
            socket.emit('leaveRoom', roomId);
        };
        */
    };

    useEffect(requestJoin, []);
}

export const useWantingRoom = (socket, userId, rooms, setRooms) => {
    const ms = useMemo(()=>socket, [socket]);

    const roomData = () => {
        ms.emit('roomListDataRequest', userId, (res) => {
            setRooms(res.rooms);
        })
        ms.on('updateRoomList', (room)=>{
            const users = room.participants;

            console.log(rooms);
            users.forEach(u => {
                if (u.id === userId) {
                    setRooms([...rooms, room]);
                }
            });
        });
    };

    useEffect(roomData, []);
}
