import React, { useState } from 'react';
import Room from '../components/Room';
import Chat from '../components/Chat';

import DesktopLayout from '../components/layout/DesktopLayout';
import io from 'socket.io-client';

const roomSocket = io.connect('http://localhost:3065/room', {
    withCredentials: true,
});

const chatSocket = io.connect('http://localhost:3065/chat', {
    withCredentials: true,
});

const Messages = () => {
    const [ room, setRoom ] = useState(null);

    return(
        <DesktopLayout Side={<Room height={document.documentElement.clientHeight} socket={roomSocket} setRoom={setRoom}/>}>
            <Chat room={room} chatSocket={chatSocket}/>
        </DesktopLayout>
    );
}


export default Messages;
