import React from 'react';
import { Input, Button } from 'antd';
import { ArrowUpOutlined } from '@ant-design/icons';


const Chat = ({ room, chatSocket }) => {

    return (
        <>
        {room ? 
        <div style={{ height:"100%", width:"100%"}}>
        <div style={{height: "10%", borderBottom: "solid thin #EFEEF5", display:"flex", alignItems:"center", justifyContent:"center"}}>
            <h2>{room.title}</h2>&nbsp;&nbsp;<h2 style={{color: "grey"}}>{room.participants.length}</h2>
        </div> 
            <div style={{position:"fixed", height:"8%", width:"40%", bottom:"0", display:"flex", alignItems: "center", borderTop: "solid thin #EFEEF5"}}>
                <div style={{width:"80%"}}><Input bordered={false} /></div>
                <div><Button type="primary" shape="circle" icon={<ArrowUpOutlined />} size='large' /></div>
                
            </div>
        </div>
         :  
        <div>
        <h1>You don’t have a message selected</h1>
        <br />
        <p>Choose one from your existing messages, or start a new one.</p>
        </div>}
        </>
    );
}

export default Chat;

/*
