import React from 'react';
import PropTypes from 'prop-types';
import { useSelector } from 'react-redux';
import { Card, Popover, Button } from 'antd';
import { MessageOutlined, EllipsisOutlined, HeartOutlined, RetweetOutlined } from '@ant-design/icons';

const PostCard = ({ post }) => {
    const { me } = useSelector((state) => state.user);
    const id = me?.id; //있으면 id 없으면 undefined
    return(
        <div>
            <Card
            cover={post.Images[0] && <PostImages images={post.Images}/>}
            actions={[
                <RetweetOutlined key="retweet" />,
                <HeartOtluined key="heart" />,
                <MessageOutlined key="comment"/>,
                <Popover key="more" content={(
                    <Button.Group>
                        {id && post.User.id === id 
                        ? (
                            <>
                            <Button>수정</Button>
                            <Button type="danger">삭제</Button>
                        </>
                        )
                     : <Button>신고</Button>}
                    </Button.Group>
                )}
                >
                    <EllipsisOutlined />
                </Popover>,
            ]}
            >
                <Card.Meta
                avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                title={post.User.nickname}
                description={post.content}
                />
                <Image />
                <Content />
                <Buttons>

                </Buttons>
            </Card>
            <CommentForm />
            <Comments />
        </div>          
    );
};

PostCard.PropTypes = {
    post: PropTypes.shape({ //shape을 이용하면 더 정확하게 prop정의 할수있다.
        id:PropTypes.number,
        User:PropTypes.object,
        content:PropTypes.string,
        createdAt:PropTypes.object,
        Comments:PropTypes.arrayOf(PropTypes.any),
        Images: PropTypes.arrayOf(PropTypes.any)
    }),
};

export default PostCard;
