/* eslint-disable */
import React, { useEffect, useState } from 'react';
import { Input, Button } from 'antd';
import { ArrowUpOutlined, LoadingOutlined } from '@ant-design/icons';
import { useSelector } from 'react-redux';
import { socket } from '../utils/io';

const Chat = ({ roomId }) => {
    console.log("re rendered");
    const { rooms } = useSelector((state)=>state.chat);
    const { me } = useSelector((state)=>state.user);
    const [room, setRoom] = useState();
    const [message, setMessage] = useState(''); //입력 메세지
    const [messages, setMessages] = useState([]); //채팅방 메세지 기록

    /*
    useEffect(()=>{
        console.log("setMessages done");

        socket.emit('loadMessages', {
            roomId : parseInt(roomId, 10),
        });
        //맨 처음 렌더링시만 실행 되도록 한다. if (!room)
        
        rooms.forEach(r => {
            if (r.id === parseInt(roomId, 10)) {
                setRoom(r);
                setMessages(r.Chats);
            }
        });
        
    }); //[]: 업데이트 되기 직전에 실행 & 언마운트 될때 실행
*/

    useEffect(()=>{
        if (!room) {
            socket.emit('loadRoom', {
                roomId : parseInt(roomId, 10),
            });
        }
    });

    useEffect(()=>{
        socket.on('roomData', (room)=>{
            setRoom(room);
        });
    })

    useEffect(()=>{
        
        socket.on('newMessage', (fullChats) => {
            /* fullChats
            [{
                id: 29,
                chat: '2시 9분',
                createdAt: 2022-01-27T05:09:02.000Z,
                updatedAt: 2022-01-27T05:09:02.000Z,
                UserId: 3,
                RoomId: 74
              },]
            */

            setMessages(fullChats);
        });

        return () => {
            socket.off('newMessage');
        };

    }, []);

    const onChangeMessage = (e) => {
        setMessage(e.target.value);
    }

    const onClickSubmit = () => {
        if (message.length > 0) {
            console.log(`send ${message} to server`);
            socket.emit('sendMessage', {
                roomId : parseInt(roomId, 10),
                message,
                userId: me.id
            });
            setMessage('');
        }
    };
    /*
    useEffect(()=>{
        if (roomId !== 'main') {
            dispatch({
                type: LOAD_ROOM_REQUEST,
                data: roomId
            });
        }
        
    }, [roomId]);
    */

    return (
        <>
        { roomId !== "main" ? 
        <div style={{ height:"100%", width:"100%"}}>
            
            {
                !room ?
                <LoadingOutlined spin/>:
                <div>
                <div style={{height: "10%", borderBottom: "solid thin #EFEEF5", display:"flex", alignItems:"center", justifyContent:"center"}}>
                <h2>{room.title}</h2>&nbsp;&nbsp;<h2 style={{color: "grey"}}>{room.participants.length}</h2>
                </div>
                {messages ? 
                    messages.map((m) => {
                        return (<p>{m.chat}</p>);
                    }) :
                    null
                }
                <div style={{position:"fixed", height:"8%", width:"40%", bottom:"0", display:"flex", alignItems: "center",
            justifyContent:"space-between", borderTop: "solid thin #EFEEF5"}}>
                <Input bordered={false} style={{width:"90%"}} onChange={onChangeMessage}/>
                <Button type="primary" shape="circle" icon={<ArrowUpOutlined />} size="large" style={{marginRight: "5%"}} onClick={onClickSubmit}/>
            </div>
            </div>
            }
            
        </div>
         :  
        <div style={{display:"flex", justifyContent:"center", alignItems:"center", width:"100%", height:"100%"}}>
        <div style={{ height:"50%", width:"50%"}}>
        <h1>You don’t have a message selected</h1>
        <br />
        <p>Choose one from your existing messages, or start a new one.</p>
        </div>
        </div>}
        </>
    );
}

export default Chat;

/*
<div>
        <h1>You don’t have a message selected</h1>
        <br />
        <p>Choose one from your existing messages, or start a new one.</p>
        </div>
 */
