import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/router';
import { PlusOutlined } from '@ant-design/icons';
import { Button, List, Avatar, Modal, Input, Tag } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { SEARCH_USER_REQUEST, LOAD_MY_INFO_REQUEST} from '../reducers/user';
import styles from './Room.module.css'

import CheckBox from './CheckBox';

import { socket } from '../utils/io';
import {UPDATE_ROOM_LIST} from '../reducers/chat';
//import { useWantingRoom } from '../hooks/chatHooks';

const Room = ({ height }) => {
    //서버사이드렌더링 - pre rendering 해야할 필요가 있을까?
    const dispatch = useDispatch();
    const router = useRouter();
    const { searchUserResult, me } = useSelector((state) => state.user);
    const { rooms } = useSelector((state)=>state.chat);
    const [ visible, onChangeVisible ] = useState(false);
    const [ groupChat, setGroupChat ] = useState({});

    useEffect(() => {

        socket.on('updateRoomList', (room) => {
            
            console.log(rooms);
            room.participants.forEach(u => {
                if (u.id === me.id) {
                    console.log('update room list');
                    //draft.me.Followings.push({ id : action.data.UserId});
                    //console.log([...rooms, room]);
                    dispatch({
                        type: UPDATE_ROOM_LIST,
                        data: room
                    });
                }
            });
        });
    },[]);

    const showModal = () => {
        onChangeVisible(true);
    }

    const handleCancel = () => {
        onChangeVisible(false);
    }

    const handleOk = () => {

        const users =[];
        Object.entries(groupChat).forEach(v=>{
            if (v[1] === true) {
                users.push(v[0]);
            }
        });
        users.push(me.id.toString());

        socket.emit('createRoomRequest', me.id, users);
        console.log('create room request');
        onChangeVisible(false);
        setGroupChat({});
        /*
        dispatch({
            type: CREATE_CHAT_REQUEST,
            data: { "users": users }
        });
        */
    }

    const onSearch = (e) => {
        dispatch({
            type: SEARCH_USER_REQUEST,
            data: e
        });
    }
    const handleChange = ({ target: { label, checked }}) => {
        setGroupChat({ ...groupChat, [label]: checked })
    }

    //채팅방 클릭했을때 채팅방 load
    const onClickRoom = (room) => {
        return router.push(`/message/${room.id}`);
    }

    return (
        <div>
        <div className="test">

        </div>
        <div className="messageTitle" style={{height: "50px", borderBottom: "solid thin #EFEEF5", display: "flex"}}>
            <div style={{width: "600px", display:"flex"}}>
                <div style={{display:"flex", alignItems:"center", flexGrow: "1"}}>
                    <div>
                        <h2 style={{margin:"0"}}>&nbsp;&nbsp;Messages</h2>
                    </div>
                </div>
                <div style={{display:"flex", alignItems:"center", flexGrow: "0"}}>
                    <div>
                        <Button type="text" shape="circle" onClick={showModal} icon={<PlusOutlined />} style={{margin: "0 15px"}} size="large"></Button>
                    </div>
                    <Modal
                    visible={visible}
                    title="New Messages"
                    onOk={handleOk}
                    onCancel={handleCancel}
                    bodyStyle={{height: "500px"}}
                    footer={[
                        <Button key="submit" type="primary" onClick={handleOk}>
                        Next
                        </Button>,
                        ]}
                    >
                    <Input.Search placeholder="Search People" onSearch={onSearch} bordered={false}  />

                
                    <div style={{display:"flex", flexDirection:"column"}}>
                    {searchUserResult.map(user => (
                        <div className={styles.userList}>
                        <CheckBox 
                        user={user}
                        handleChange={handleChange}
                        label={user.id}
                        value={groupChat[user.id]}
                        />
                        </div>))}
                    </div>

                    </Modal>
                </div>
            </div>
        </div>

        <div id="scrollableDiv" style={{height: height-50 , overflow: 'auto'}}>
        <List
        itemLayout="vertical"
        dataSource={rooms}
        renderItem={item => (
        <div className={styles.roomList} onClick={()=>onClickRoom(item)}>
        <List.Item
            key={item.id}>
            <List.Item.Meta
            title={item.title}
            />
        {item.title}
      </List.Item>
      </div>
      )}
      />
        </div>

        </div>
    );
}

export default Room;
