import React, { useCallback, useState, useEffect } from 'react';
import Head from 'next/head';
import { Form, Input, Checkbox, Button } from 'antd';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import { useRouter } from 'next/router';
import Link from 'next/link';
import AppLayout from '../components/appLayout';
import useInput from '../hooks/useInput';
import { SIGN_UP_REQUEST, SIGN_UP_DONE, LOAD_MY_INFO_REQUEST} from '../reducers/user';
import wrapper from '../store/configureStore';
import axios from 'axios';
import { END } from 'redux-saga';
const ErrorMessage = styled.div`
color: red;
`;

const contentStyle = { 
    border: "1px solid #E0E0E0", 
    height: "450px", 
    width: "500px", 
    margin: "auto", 
    marginTop: "100px", 
    background:"white"
};

const Signup = () => {
    const router = useRouter();
    const dispatch = useDispatch();
    const {  signUpLoading, signUpDone, signUpError, me } = useSelector((state)=>state.user);

    //useEffect
    //리액트컴포넌트가 렌더링 될때마다 특정 작업을 실행할 수 있도록 하는 Hook
    useEffect(()=>{
        if (me?.id) { 
            alert('이미 로그인 하셨습니다.');
            router.push('/');
        }
    }, [me?.id]);

    useEffect(()=>{
        if (signUpDone) {
            router.push('/');
        }
    }), [signUpDone];

    useEffect(()=>{
        if (signUpError) {
            alert(signUpError);
        }
    },[signUpError])


    const [email, onChangeEmail] = useInput('');
    const [nickname, onChangeNickname] = useInput('');
    const [password, onChangePassword] = useInput('');

    const [passwordCheck, setPasswordCheck] = useState('');
    const [passwordError, setPasswordError] = useState(false);//passwordError가 원래는 false였음
    const onChangePasswordCheck = useCallback((e)=>{
        setPasswordCheck(e.target.value);
        setPasswordError(e.target.value !== password);//다르면 password를 true로 set한다
    }, [password]);

    const [term, setTerm] = useState('');
    const [termError, setTermError] = useState(false);
    const onChangeTerm = useCallback((e) => {
        setTerm(e.target.checked);
        setTermError(false);
    }, []);

    const onSubmit = useCallback(()=>{
        if(password !== passwordCheck){
            return setPasswordError(true);
        }
        if(!term) {
            return setTermError(true);
        }
        
        dispatch({
            type: SIGN_UP_REQUEST,
            data: { email, password, nickname },
        });
        
    },[password,passwordCheck,term]);

    return (
        <>
        <Head>
        <title>Chatter에 가입하기</title>
        </Head> 
        <div style={contentStyle}>
            <h1 style={{textAlign: "center", margin: "10px 0", fontFamily: "Righteous, cursive", fontSize: "50px"}}>Chatter</h1>
            <h2 style={{textAlign: "center", fontWeight: "bolder", color: "grey"}}>새 계정 만들기</h2>
            <div className="loginForm" style={{width:"300px", margin:"10px auto"}}>
                <Form >
                    <div>
                        <Input 
                        name="user-email" 
                        type="email"
                        placeholder="이메일"
                        size="large"
                        style={{width:"300px", margin:"5px auto"}} 
                        value={email} 
                        onChange={onChangeEmail} 
                        required />
                    </div>
                    <div>
                    <Input 
                            name="user-nickname" 
                            type="email"
                            placeholder="닉네임"
                            size="large"
                            style={{width:"300px", margin:"5px auto"}} 
                            value={nickname} 
                            onChange={onChangeNickname} 
                            required />
                        
                    </div>
                    <div>
                        <Input.Password
                            name="user-password" 
                            type="password"
                            placeholder="비밀번호"
                            size="large"
                            style={{width : "300px", margin: "5px auto"}} 
                            value={password} 
                            onChange={onChangePassword} 
                            required />
                    </div>
                    <div>
                        <Input.Password
                            name="user-password-check" 
                            type="password"
                            placeholder="비밀번호 확인"
                            size="large"
                            style={{width : "300px", margin: "5px auto"}} 
                            value={passwordCheck} 
                            onChange={onChangePasswordCheck} 
                            required />
                            {passwordError && <ErrorMessage>비밀번호가 일치하지 않습니다.</ErrorMessage>}
                    </div>
                    <div style={{margin: "auto"}}>
                        <Button type="primary" htmlType="submit">가입하기</Button>
                    </div>
                    
                    <div style={{textAlign: "center"}}>
                        <Link href="/"><a >이미 계정이 있으신가요?</a></Link>
                    </div>
                    
                
                </Form>
            </div>
        </div>
        </>
    );
}

export const getServerSideProps = wrapper.getServerSideProps(async (context)=>{
    const cookie = context.req? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = '';
    if (context.req && cookie) {
        axios.defaults.headers.Cookie = cookie;
    }

    context.store.dispatch({
        type: LOAD_MY_INFO_REQUEST,
    });
    
    context.store.dispatch(END); //REQUEST가 SUCCESS가 될때까지 기다려준다.
    await context.store.sagaTask.toPromise();
});

export default Signup;
