import React from 'react';
import { useRouter } from 'next/router';

const Title = () => {
    const router = useRouter();

    const toTop = () => {
        router.push('/main');
        window.scrollTo({top:0, left:0, behavior:'auto'});
    };
    
    return(
        <>
        <div style={{display:"flex", alignItems:"center", height:"50px", position:"fixed", top:"0"}} onClick={toTop}>
            <h1>Home</h1>
        </div>
        </>
    );
};

export default Title;
