import React, { useEffect, useState, useRef, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Avatar, Image, Button, Modal, Input, Badge, Form } from 'antd';
import { PlusCircleFilled } from '@ant-design/icons';
import { END } from 'redux-saga';
import Head from 'next/head';
import Link from 'next/link';
import { useRouter } from 'next/router';

import axios from 'axios';
import { LOAD_USER_POSTS_REQUEST } from '../../reducers/post';
import { LOAD_MY_INFO_REQUEST, LOAD_USER_REQUEST } from '../../reducers/user';
import PostCard from '../../components/PostCard';
import wrapper from '../../store/configureStore';
import DesktopLayout from '../../components/layout/DesktopLayout';

const User = () => {
  const imageInput = useRef();
  const dispatch = useDispatch();
  const router = useRouter();
  const { id } = router.query;
  const { mainPosts, hasMorePosts, loadPostsLoading } = useSelector((state) => state.post);
  const { userInfo, me } = useSelector((state) => state.user);
  const [modalVisible, setmodalVisible] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      if (window.pageYOffset + document.documentElement.clientHeight > document.documentElement.scrollHeight - 300) {
        if (hasMorePosts && !loadPostsLoading) {
          dispatch({
            type: LOAD_USER_POSTS_REQUEST,
            lastId: mainPosts[mainPosts.length - 1] && mainPosts[mainPosts.length - 1].id,
            data: id,
          });
        }
      }
    };
    window.addEventListener('scroll', onScroll);
    return () => {
      window.removeEventListener('scroll', onScroll);
    };
  }, [mainPosts.length, hasMorePosts, id, loadPostsLoading]);

  const onClickEditProfile = () => {
    setmodalVisible(true);
  }
  const onClickClose = () => {
    setmodalVisible(false);
  }

  const onClickSave = () => {

  }

  const onClickEditProfilePhoto = useCallback(() => {
    imageInput.current.click();
  }, [imageInput.current]);

  const onChangePhotos = useCallback((e) => {
    console.log(e);
  }, []);

  return (
    <DesktopLayout>
      {userInfo && (
        <Head>
          <title>
            {userInfo.nickname}
            님의 글
          </title>
          <meta name="description" content={`${userInfo.nickname}님의 게시글`} />
          <meta property="og:title" content={`${userInfo.nickname}님의 게시글`} />
          <meta property="og:description" content={`${userInfo.nickname}님의 게시글`} />
          <meta property="og:image" content="https://nodebird.com/favicon.ico" />
          <meta property="og:url" content={`https://nodebird.com/user/${id}`} />
        </Head>
      )}
      {userInfo
        ? (
          <>
          <div style={{display:"flex", alignItems:"center", justifyContent: "space-between", margin:"20px"}}>
                <div style={{display: "flex", alignItems:"center", justifyContent: "center"}}>
                    <Avatar src={<Image src="https://joeschmoe.io/api/v1/random"/>} size={128}/>
                    <div style={{display: "inline-block"}}>
                        <h2>{userInfo.nickname}</h2>
                        <p>{userInfo.email}</p>
                    </div>
                </div>
                {me && me.id==userInfo.id ?
                <Button shape="round" onClick={onClickEditProfile}>Edit profile</Button>:
                null}
            </div>
            <div style={{display:"flex", margin:"20px"}}>
                <p>{userInfo.Followings} Following {userInfo.Followers} Followers</p>
            </div>
            <Modal
          visible={modalVisible}
          title="Edit profile"
          onCancel={onClickClose}
          footer={[
            <Button key="submit" shape="round" style= {{color: "black"}} onClick={onClickSave}>
              Save
            </Button>,
          ]}
        >
          <Form style={{ margin: '10px 0 20px' }} encType="multipart/form-data" onFinish={onSubmit}>
        <Input.TextArea value={text} onChange={onChangeText} maxLength={140} placeholder="어떤 신기한 일이 있었나요?" bordered={false}/>
        <div style={{ margin: '10px 0'}}>
          <input type="file" name="image" hidden ref={imageInput} onChange={onChangePhotos} />
          
          <Button type="primary" shape="round" style={{ float: 'right' }} htmlType="submit">업로드</Button>
        </div>
      </Form>

          <div onClick={{onClickEditProfilePhoto}}>
          <input type="file" name="photo" hidden ref={imageInput} onChange={onChangeImages} />
          <Badge count={<PlusCircleFilled style={{ color: 'red' }} style={{fontSize:"25px"}}/>}  offset={[-10, 110]}>
          <Avatar src="https://joeschmoe.io/api/v1/random" size={128}/>
          </Badge>
          </div>
          <Input placeholder="Name" />
        </Modal>
          </>
        )
        : null}
      {mainPosts.map((post) => <PostCard key={post.id} post={post} />)}
    </DesktopLayout>
  );
};

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
  const cookie = context.req ? context.req.headers.cookie : '';
  axios.defaults.headers.Cookie = '';
  if (context.req && cookie) {
    axios.defaults.headers.Cookie = cookie;
  }
  context.store.dispatch({
    type: LOAD_USER_POSTS_REQUEST,
    data: context.params.id,
  });
  context.store.dispatch({
    type: LOAD_MY_INFO_REQUEST,
  });
  context.store.dispatch({
    type: LOAD_USER_REQUEST,
    data: context.params.id,
  });
  context.store.dispatch(END);
  await context.store.sagaTask.toPromise();
  //console.log('getState', context.store.getState().post.mainPosts);
  return { props: {} };
});

export default User;
