import React, { useState } from 'react';
import { useRouter } from 'next/router';
import io from 'socket.io-client';
import { useSelector } from 'react-redux';

import Room from './../../components/Room';
import Chat from './../../components/Chat';
import DesktopLayout from './../../components/layout/DesktopLayout';


const roomSocket = io.connect('http://localhost:3065/room', {
    withCredentials: true,
});

const chatSocket = io.connect('http://localhost:3065/chat', {
    withCredentials: true,
});

const Message = () => {
    const router = useRouter();
    const { roomId } = router.query;
    const { me } = useSelector((state)=>state.user);
    const [ room, setRoom ] = useState(null);
    
    useEffect(()=>{

        me.participateRoom.forEach(r => {
            if (r.id === roomId){
                setRoom(r);
            }
        });

    }, [me]);

    return(
        <DesktopLayout Side={<Room height={document.documentElement.clientHeight} socket={roomSocket}/>}>
            <Chat roomId={roomId} chatSocket={chatSocket}/>
        </DesktopLayout>
    );
}

export default Message;
